    <div className="hstieubieucuanuoc">
    <aside className="aside2">
      <div>
        <figure style={{background: '#fff', height: '38px'}}>
          <h2>
            <a href>Học sinh tiêu biểu</a> 
          </h2>
        </figure>
        {this.renderItem()}
      </div>
    </aside>
  </div>

    )
  }
}

export default GoodStudent;
